/* MODULE US. */

#include "Events.h"
#include "US.h"


static bool EnUser;                    /* Enable/Disable device by user */
volatile word US_CntrState;            /* Content of counter */


/*
** ===================================================================
**     Method      :  US_Enable (component Capture)
**     Description :
**         This method enables the component - it starts the capture.
**         Events may be generated (<DisableEvent>/<EnableEvent>).
**     Parameters  : None
**     Returns     :
**         ---             - Error code, possible codes:
**                           ERR_OK - OK
**                           ERR_SPEED - This device does not work in
**                           the active speed mode
** ===================================================================
*/
byte US_Enable(void)
{
  US_CntrState = TPM3CNT;              /* Load content of counter register to variable CntrState */
  if (!EnUser) {                       /* Is the device disabled by user? */
    EnUser = TRUE;                     /* If yes then set the flag "device enabled" */
    /* TPM3C3SC: CH3F=0,CH3IE=1,MS3B=0,MS3A=0,ELS3B=1,ELS3A=1,??=0,??=0 */
    setReg8(TPM3C3SC, 0x4CU);          /* Enable both interrupt and capture function */ 
  }
  return ERR_OK;                       /* OK */
}

/*
** ===================================================================
**     Method      :  US_Disable (component Capture)
**     Description :
**         This method disables the component - it stops the capture.
**         No events will be generated.
**     Parameters  : None
**     Returns     :
**         ---             - Error code, possible codes:
**                           ERR_OK - OK
**                           ERR_SPEED - This device does not work in
**                           the active speed mode
** ===================================================================
*/
byte US_Disable(void)
{
  if (EnUser) {                        /* Is the device enabled by user? */
    EnUser = FALSE;                    /* If yes then set the flag "device disabled" */
    /* TPM3C3SC: CH3F=0,CH3IE=0,MS3B=0,MS3A=0,ELS3B=0,ELS3A=0,??=0,??=0 */
    setReg8(TPM3C3SC, 0x00U);          /* Disable capture function */ 
  }
  return ERR_OK;                       /* OK */
}

