ISR(US_Interrupt)
{
  (void)TPM3C3SC;                      /* Dummy read to reset interrupt request flag */
  /* TPM3C3SC: CH3F=0 */
  clrReg8Bits(TPM3C3SC, 0x80U);        /* Reset interrupt request flag */ 
  Cap1_OnCapture();                    /* Invoke user event */
}



